import React, {useState} from 'react';
import validateEmail from './functions/sendCode';
import validateUser from './functions/validateUser';


function Form(props) {
    let [username, setUsername] = useState("");
    let [email, setEmail] = useState("");
    let [password, setPassword] = useState("");
    let [confirmPassword, setConfirmPassword] = useState("");
    async function handleSignUp () {
        if(password !== confirmPassword) {
            props.setMessage("Passwords do not match");
            props.notify();
        }else{
            if(password.length < 8) {
                props.setMessage("Password must be at least 8 characters long");
                props.notify();
            }else{
                let response = await validateEmail(username);
                if(response.status === "success") {
                    props.setAction("confirm");
                }else{
                    props.setMessage( String(response.error) );
                    props.notify();
                }

            }
        }
    }
    async function handleSignIn() {
        let response = await validateUser(username, password);
        if(response.authenticated) {
            window.location.href = "/home";
        }else{
            props.setMessage(response.message);
            props.notify();
        }
    }
    return (<div className={"form"}>
        <div className={"form-field"}>
            <label htmlFor="username">Username:</label>
            <input type="text" onChange={(e)=>{
                setUsername(e.target.value);
            }} value={username} name="username" />
        </div>
        {(props.action === "signup") ?
            <div className={"form-field"}>
                <label htmlFor="email">Email:</label>
                <input onChange={(e)=>{
                setEmail(e.target.value);
            }} value={email} name={"email"} type="email" />
            </div>
            : <div></div>}
        <div className={"form-field"}>
            <label htmlFor="password">Password:</label>
            <input onChange={(e)=>{
                setPassword(e.target.value);
            }} value={password} type="password" />
        </div>
        {(props.action === "signup") ?
            <div className={"form-field"}>
                <label htmlFor="confirmPassword">Confirm Password:</label>
                <input onChange={(e)=>{
                setConfirmPassword(e.target.value);
            }} value={confirmPassword} name={"confirmPassword"} type="password" />
            </div>
            : <div></div>}
        <button onClick={()=>{
            if(props.action === "signup") {
                handleSignUp();
            }else {
                handleSignIn();
            }
        }} className={"form-submit-button"}>Submit</button>
    </div>);
}

export default Form;
